# input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
input = ""

def advance_slice(chunk: slice) -> slice:
    return slice(chunk.start + 1, chunk.stop + 1)

def all_unique(chars: str) -> bool:
    return len(set(chars)) == len(chars)

# part 1
pos = 4
chunk = slice(0, pos)

while True:
    chars = input[chunk]

    if all_unique(chars):
        break
    else:
        pos += 1
        chunk = advance_slice(chunk)

# answer 1
print(pos)

